import styled from 'styled-components';
import { COLOR } from '@config/constant';
import { Divider, Typography, Skeleton, Space, Row, Col } from 'antd';
import React from 'react';
import moment from 'moment';

const { Title } = Typography;

export const DetailLayout = props => {
  const { loading, detail } = props;

  if (!detail) return <></>;

  const descList = [
    {
      title: '年龄',
      value: detail.starAge + '岁',
    },
    {
      title: '身高',
      value: detail.starHeight + 'cm',
    },
    {
      title: '体重',
      value: detail.starWeight + 'kg',
    },
    {
      title: '生日',
      value: moment(detail.starDate).format('YYYY-MM-DD'),
    },
  ];

  return (
    <Wrapper>
      <Row style={{ maxWidth: '1280px' }}>
        <Col span={12} style={{ display: 'flex', justifyContent: 'center' }}>
          {loading ? <Skeleton.Image active /> : props.gallery}
        </Col>
        <Col span={12}>
          {loading ? (
            <Skeleton active />
          ) : (
            <>
              <Name>{detail.starName}</Name>
              <Descriptions>{detail.starBriefIntroduction}</Descriptions>
              <Divider style={{ borderTop: '2px solid #e0e0e0' }} />
              {descList.map(item => (
                <React.Fragment key={item.title}>
                  <Space>
                    <Title level={5} style={{ margin: 0, fontWeight: 600 }}>
                      {item.title}
                    </Title>
                    <Title level={5} style={{ margin: 0, fontWeight: 400 }}>
                      {item.value}
                    </Title>
                  </Space>
                  <br />
                </React.Fragment>
              ))}
            </>
          )}
        </Col>
      </Row>
      <Row>
        <Experience>
          {(detail.experience || []).map(item => (
            <AwardItem key={item.title}>
              <ALeft>
                <div
                  style={{
                    height: '200px',
                    width: '180px',
                    backgroundImage: `url(${item.img})`,
                    backgroundPosition: 'center',
                    backgroundSize: 'cover',
                    backgroundRepeat: 'no-repeat',
                  }}
                />
                {/* <Image
                  width={200}
                  height={200}
                  src={item.img}
                  fallback="data:image/png;base64,"
                /> */}
              </ALeft>
              <ARight>
                <Typography.Title level={5} style={{ margin: 0, fontWeight: 400 }}>
                  <a href={item.url} rel="noreferrer" target={'_blank'}>
                    {item.title}
                  </a>
                </Typography.Title>
                {/* <Paragraph>{item.desc}</Paragraph> */}
              </ARight>
            </AwardItem>
          ))}
        </Experience>
      </Row>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  overflow-y: auto;
  padding-top: 64px;
`;

const Experience = styled.div`
  max-width: 1280px;
  position: relative;
  flex: 1;
  display: flex;
  flex-direction: column;
`;

const Name = styled.div`
  font-size: 1.4rem;
  font-weight: 400;
  color: ${COLOR.DetailTitle};
`;

const Descriptions = styled.div`
  color: ${COLOR.DetailDesc};
`;

const ALeft = styled.div`
  flex: 1;
`;

const ARight = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  margin-left: 40px;
`;

const AwardItem = styled.div`
  max-width: 800px;
  display: flex;
  margin-top: 20px;
`;
