import React from 'react';
import styled from 'styled-components';
import moment from 'moment';
import { Modal, Typography, Image, Row, Col } from 'antd';

const { Title } = Typography;

export const DetailModal = props => {
  const { detail, onClose } = props;

  const descList = detail
    ? [
        {
          title: '姓名',
          value: detail.starName,
        },
        {
          title: '年龄',
          value: detail.starAge + '岁',
        },
        {
          title: '身高',
          value: detail.starHeight + 'cm',
        },
        {
          title: '体重',
          value: detail.starWeight + 'kg',
        },
        {
          title: '生日',
          value: moment(detail.starDate).format('YYYY-MM-DD'),
        },
        {
          title: '语言',
          value: detail.starLanguage,
        },
        // {
        //   title: '爱好',
        //   value: detail.starHobby,
        // },
        {
          title: '地区',
          value: detail.starNation,
        },
        {
          title: '已验证',
          value: detail.certify ? '是' : '否',
        },
        {
          title: '自我介绍',
          value: detail.starBriefIntroduction,
        },
        {
          title: '创建者',
          value: detail.createBy,
        },
        {
          title: '创建时间',
          value: detail.createTime,
        },
        {
          title: '更新时间',
          value: detail.updateTime,
        },
      ]
    : [];

  return (
    <Modal
      title="详细信息"
      open={!!detail}
      maskClosable={false}
      centered={true}
      bodyStyle={{
        maxHeight: '70vh',
        overflowY: 'auto',
      }}
      width={800}
      footer={null}
      onCancel={onClose}
    >
      <Wrapper>
        {descList.map(item => (
          <Row key={item.title} style={{ marginTop: '10px' }}>
            <Col span={3}>
              <Title
                level={5}
                style={{ margin: 0, fontWeight: 600, width: '100px' }}
              >
                {item.title}
              </Title>
            </Col>
            <Col span={21}>
              <Title level={5} style={{ margin: 0, fontWeight: 400 }}>
                {item.value}
              </Title>
            </Col>
          </Row>
        ))}

        <Experience>
          <ExperienceItem>
            <Image
              width={100}
              height={100}
              src={detail?.starMasterImg}
              fallback="data:image/png;base64,"
            />
            <Title level={5} style={{ marginTop: '10px', fontWeight: 400 }}>
              {'照片'}
            </Title>
          </ExperienceItem>
          <ExperienceItem>
            <Image
              width={100}
              height={100}
              src={detail?.starHead}
              fallback="data:image/png;base64,"
            />
            <Title level={5} style={{ marginTop: '10px', fontWeight: 400 }}>
              {'头像'}
            </Title>
          </ExperienceItem>
        </Experience>

        <Title level={5} style={{ margin: 0, fontWeight: 600 }}>
          {'代表作品'}
        </Title>
        <Experience>
          {detail?.experience.map(item => (
            <ExperienceItem key={item.title}>
              <Image
                width={100}
                height={100}
                src={item.img}
                fallback="data:image/png;base64,"
              />
              <Title level={5} style={{ marginTop: '10px', fontWeight: 400 }}>
                <a href={item.url} target="_blank" rel="noopener noreferrer">
                  {item.title}
                </a>
              </Title>
            </ExperienceItem>
          ))}
        </Experience>
      </Wrapper>
    </Modal>
  );
};

const Wrapper = styled.div``;
const Experience = styled.div`
  display: flex;
  flex-wrap: wrap;
`;
const ExperienceItem = styled.div`
  /* display: flex; */
  align-items: center;
  justify-content: center;
  margin: 10px 30px 0 0;
`;
